import React, { useState } from 'react'
import { Button, Form } from 'react-bootstrap';
import token from './GetToken';
import GetDate from '../GetDate';
import axios from 'axios';
import config from './Config.json';
import { ToastContainer, toast } from 'react-toastify';
import Loader from '../Loader';

export default function AddBlog() {

    const [blogName, setBlogName] = useState("");
    const [blogDescription, setBlogDescription] = useState("");
    const [blogPublished, setBlogPublished] = useState("yes");
    const [isLoading, setIsLoading] = useState(false);
    const [image, setImage] = useState(null);
    var jwt = token();

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        setImage(file);
    }

    const handlePublishChange = (e) => {
        setBlogPublished(e.target.value);
    }

    const handleSubmit = async (e) => {
        e.preventDefault();

        var blogImgData = null;
        setIsLoading(true);
        try
        {
            if(image !== null && jwt != "")
            {
                let formData = new FormData();
                formData.append('files',image);
                
                let upload_response = await axios({
                    method: 'POST',
                    url:`${config.baseURL}/api/upload`,
                    headers: { 
                        'accept' : 'application/json',
                        'content-type': 'multipart/form-data',
                        "Authorization":`Bearer ${jwt}`
                    },
                    data: formData,
                    onUploadProgress:(progress)=>{ 
                        console.log("FileUpload ", progress);
                    }
                });
                if(upload_response.status === 200){
                    // console.log('file upload response ',upload_response);
                    blogImgData = upload_response.data[0];
                    setIsLoading(false);
                }
            }
        } catch (error) {
            blogImgData = null;
            setIsLoading(false);
        }

        let payload = {
            name : blogName,
            description : blogDescription,
            created_on : GetDate(),
            published : blogPublished,
            image: blogImgData
        }

        try{
            axios({
                method: 'POST',
                url: `${config.baseURL}/api/blogs`,
                data: {"data": payload}  
            })
            .then(function (response) {
                console.log(response.data);
                if(response.data){
                    // toast.success("Blog updated successfully");
                    window.location.href = "/admin/blog-list";
                }
                else{
                    setIsLoading(false);
                    toast.error("Blog creation failed");
                    return false;
                }
            })
            .catch(function (error) {
                // handle error
                setIsLoading(false);
                console.log(error);
                toast.error(error.message);
                return false;
            });

        }catch(error){
            setIsLoading(false);
            console.log(error);
        }
    }
    const removeBlogImg = (e) => {
        document.querySelector('.blog-image-wrap_'+blogId+" .blog-image").innerHTML = '';
        document.querySelector('#blogImage').value = '';
        setImage(null);
    }

    const revertBlogImg = (e) => {
        var img_id = e.target.getAttribute('data-img-id');
        var imgThumbClass = 'img-thumb_'+img_id;
        if( document.querySelector('.img-thumb').classList.contains(imgThumbClass) ){
            document.querySelector('.'+imgThumbClass).style.display = 'block';
        }
        else{
            if(blogImg!==null){
                var elmt = '<img class="img-thumb img-thumb_'+blogImgId+'" src="' + config.baseURL+blogImg + '" />';
                document.querySelector('.blog-image-wrap_'+blogId+" .blog-image").innerHTML = elmt;
                document.querySelector('#blogImage').value = '';
                setImage(null);
            }
        }
        setIsImage(true);
    }

    return (
        <>
            {isLoading ? <Loader/> : null}
            <Form onSubmit={(e) => handleSubmit(e)}>
                <Form.Group className="mb-3" controlId="blogName">
                    <Form.Label>Name</Form.Label>
                    <Form.Control type="text" placeholder="Blog Name" value={blogName} onChange={(e)=>setBlogName(e.target.value)}/>
                </Form.Group>
                <Form.Group className="mb-3" controlId="blogDescription">
                    <Form.Label>Description</Form.Label>
                    <Form.Control as="textarea" rows={5} placeholder="Blog Description" value={blogDescription} onChange={(e)=>setBlogDescription(e.target.value)}/>
                </Form.Group>
                <div key={`inline-radio`} className="mb-3">
                    <Form.Label className="label-margin">Published</Form.Label>
                    <Form.Check
                        inline
                        label="Yes"
                        name="published"
                        type="radio"
                        id={`inline-radio-1`}
                        value="yes"
                        checked={blogPublished === 'yes'}
                        onChange={(e) => handlePublishChange(e)}
                    />
                    <Form.Check
                        inline
                        label="No"
                        name="published"
                        type="radio"
                        id={`inline-radio-2`}
                        value="no"
                        onChange={(e) => handlePublishChange(e)}
                    />
                </div>
                <div className="mb-3 blog-img-wrap">
                    <div className="blog-image">
                        
                    </div>
                    {image !== null && 
                    <div className="blog-img-actions py-2">
                        <span className="badge text-bg-danger" onClick={(e)=>removeBlogImg(e)}>Remove</span>
                        <span className="badge text-bg-success" onClick={(e)=>revertBlogImg(e)}>Reset</span>
                    </div>}
                </div>
                <Form.Group controlId="blogImage" className="mb-3">
                    <Form.Label>Blog Image</Form.Label>
                    <Form.Control type="file" name="file" accept="image/*" onChange={handleImageChange}/>
                </Form.Group>
                <Button className="btn btn-success btn-margin" type="submit">Submit</Button>
                <a type="button" href="/admin/blog-list" className="btn btn-secondary">Back</a>
            </Form>
            <ToastContainer/>
        </>
    )
}
