import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'

export default function BlogPost() {
    const {id} = useParams();
    const [blogName, setBlogName] = useState("");
    const [blogDescription, setBlogDescription] = useState("");
    const [blogPublished, setBlogPublished] = useState("");
    const [blogImg, setBlogImg] = useState(null);
    
    const fetchBlog = () => {
        fetch('http://localhost:1337/api/blogs/'+id+'?populate=*',{
            method : 'GET'
        })
        .then(response => response.json())
        .then((res) => {
            if(res.data){
                let blogData = res.data.attributes;
                console.log(blogData);
                setBlogName(blogData.name);
                setBlogDescription(blogData.description);
                setBlogPublished(blogData.publishedAt);

                var imageURL = null;
                if(blogData.image.data && blogData.image.data !== null){
                    imageURL = blogData.image.data.attributes.url;
                }
                setBlogImg(imageURL);
            }
        })
        .catch((error) => {
            console.log(error);
        });
    }

    useEffect(() => {
        fetchBlog();
    }, [])

    return (
        <>
            <h3 className="pb-3">{blogName}</h3>
            <p><img src={`http://localhost:1337${blogImg}`}/></p>
            <p><strong>Published:</strong> {blogPublished}</p>
            <p>{blogDescription}</p>
        </>
    )
}
