import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'

export default function BlogPost() {
    const {id} = useParams();
    const [blogName, setBlogName] = useState("");
    const [blogDescription, setBlogDescription] = useState("");
    const [blogPublished, setBlogPublished] = useState("");
    const [blogImg, setBlogImg] = useState(null);
    
    const fetchBlog = () => {
        fetch('http://localhost:1337/api/blogs/'+id+'?populate=*',{
            method : 'GET'
        })
        .then(response => response.json())
        .then((res) => {
            if(res.data){
                let blogData = res.data.attributes;
                setBlogName(blogData.name);
                setBlogDescription(blogData.description);
                setBlogPublished(blogData.published);

                var imageURL = null;
                if(blogData.image.data && blogData.image.data !== null){
                    imageURL = blogData.image.data.attributes.url;
                }
                setBlogImg(imageURL);
            }
        })
        .catch((error) => {
            console.log(error);
        });
    }

    useEffect(() => {
        fetchBlog();
    }, [])

    return (
        <h2>{blogName}</h2>
    )
}
