import React from 'react'
import $ from 'jquery';

function BtnLoader(event, status) {

    if(status == 'show'){
        $(event.target).find('.btn-spin').addClass('show');
        $(event.target).find('.btn-spin-status').html("Loading...");
        // document.querySelector('.btn-spin').classList.add('show');
        document.querySelector('.btn-spin').closest('button').classList.add('show1');
    }   
    console.log('btn component');
}

export {BtnLoader}
