import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Card, Col, Row, Table} from 'react-bootstrap'
import config from './Config.json'
var API_BASE_URL = config.API_BASE_URL;

export default function Blog() {

    const [data, setData] = useState([]);

    let blogs = () => {
        axios.get(API_BASE_URL+'/api/blogs?populate=*')
        .then(function (response) {
            // handle success
            // console.log(response.data);
            // console.log(response.data.blogImage);
            setData(response.data.data);
            // setIsLoading(false);
        })
        .catch(function (error) {
            // handle error
            // console.log(error);
            setData([]);
            // setIsLoading(false);
        })
        .finally(function () {
            // always executed
            // setIsLoading(false);
        });
    }

    useEffect(() => {
        // setIsLoading(true);
        blogs();
    }, [])

    return (
        <>
            <h3 className="pb-3">Blog</h3>
            <div className="g-4">

                <div className="row row-cols-1 row-cols-sm-2 row-cols-md-3 g-4">
            
                    {data && data.length > 0 && data.map((item, index) => {
                        let blog = item.attributes;
                        // console.log(blog);
                        var imageURL = null;
                        if(blog.image.data && blog.image.data !== null){
                            imageURL = blog.image.data.attributes.url;
                        }
                        return (
                            <div className="col" key={index}>
                                <div className="card h-100">
                                    <div className="blog-img-wrap">
                                        {imageURL !== null ? <img className="img-thumb" src={API_BASE_URL+imageURL}/> : <img className="img-thumb" src="/assets/img/no-image.jpg"/> }
                                    </div>
                                    <div className="card-body">
                                        <h5 className="card-title pb-2">{blog.name}</h5>
                                        <div className="card-text"><a href={`/blog/post/${item.id}`} className="btn btn-success btn-sm" type="button">View More</a></div>
                                    </div>
                                </div>
                            </div>
                        )
                    })}

                    {data.length == 0 && <div>No Blog found.</div>}
                
                </div>

                
            </div>
        </>
    )
}
