      setLoggedIn(true);
    }
    else{
      console.log('logged out');
      setLoggedIn(false);
    }
  }, [])
  */

  var loggedIn = false;
  if(localStorage.getItem('isLoggedIn') == 'true'){
    var loggedIn = true;
  }

  return (
    <>      
      <Header/>
      <Router>
        <Routes>
          {/* <Route path="/" element={loggedIn ? <Login/> : <Navigate replace to="/dashboard"/>}></Route>
          <Route path="/login" element={loggedIn === false ? <Login/> : <Navigate replace to="/dashboard"/>}></Route>
