          <Route path="/" element={loggedIn === true ? <Login/> : <Navigate replace to="/dashboard"/>}/>
          <Route path="/login" element={loggedIn === false ? <Login/> : <Navigate replace to="/dashboard"/>}/>
          <Route path="/register" element={loggedIn === false ? <Login/> : <Navigate replace to="/dashboard"/>}/>
          <Route path="/dashboard" element={<Dashboard/>}/>
        </Routes>
      </Router>

    </>
  );
}

export default App;
