import React, { useEffect, useState } from 'react'
import Auth from '../Auth'
import { Table } from 'react-bootstrap'
import axios from 'axios';
import {GetDate} from '..GetDate';

export default function BlogList() {

    const [data, setData] = useState([]);

    let blogs = () => {
        axios.get('http://localhost:1337/api/blogs')
        .then(function (response) {
            // handle success
            console.log(response.data.data);
            setData(response.data.data);
        })
        .catch(function (error) {
            // handle error
            // console.log(error);
            setData([]);
        })
        .finally(function () {
            // always executed
        });
    }

    useEffect(() => {
        blogs();
    }, [])
    

    if(Auth()){
        return (
            <>
                <h4 className="pb-2">BlogList</h4>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Blog Name</th>
                            <th>Published</th>
                            <th>Created On</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data && data.length ? (
                            data.map((item, index) => {
                                let blog = item.attributes;
                                return (
                                    <tr key={index}>
                                        <td>{item.id}</td>
                                        <td>{blog.name}</td>
                                        <td>{blog.published}</td>
                                        <td>{blog.created_on}</td>
                                        <td>
                                            <a href={`/edit-blog/${item.id}`} className="text-decoration-none text-success pr-5">Edit</a>
                                            <a className="text-decoration-none text-danger">Delete</a>
                                        </td>
                                    </tr>
                                )
                            })
                        ) : (<tr><td>No data found</td></tr>)}
                    </tbody>
                </Table>
            </>
        )
    }
}
