/* Validate user authorization */

export default function Auth(redirect = true) {

  if(!redirect){
    return false;
  }

  if(localStorage.getItem('isAdminLoggedIn') === null){
    window.location.href = "/admin/login";
  }
  else{
    return true;
  }
}
