import logo from './logo.svg';
import { BrowserRouter as Router, Routes, Route, Navigate} from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import Dashboard from './components/Dashboard';
import Header from './components/layouts/Header';
import BlogList from './components/admin/BlogList';
import { Container, Row } from 'react-bootstrap';

function App() {

  /* check user logged in or not */
  var loggedIn = false;
  if(localStorage.getItem('isLoggedIn') == 'true'){
    var loggedIn = true;
  }

  return (
    <>      
      <Header/>
      <Container className="mt-3">
        <Row>
          <Router>
            <Routes>
              <Route path="/" element={!loggedIn ? <Login/> : <Navigate replace to="/dashboard"/>}/>
              <Route path="/login" element={!loggedIn ? <Login/> : <Navigate replace to="/dashboard"/>}/>
              <Route path="/register" element={!loggedIn ? <Register/> : <Navigate replace to="/dashboard"/>}/>
              <Route path="/dashboard" element={<Dashboard/>}/>
              <Route path="/blog-list" element={<BlogList/>}/>
            </Routes>
          </Router>
        </Row>
      </Container>
    </>
  );
}

export default App;
