import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Card, Col, Row, Table} from 'react-bootstrap'

export default function Blog() {

    const [data, setData] = useState([]);

    let blogs = () => {
        axios.get('http://localhost:1337/api/blogs?populate=*')
        .then(function (response) {
            // handle success
            // console.log(response.data);
            // console.log(response.data.blogImage);
            setData(response.data.data);
            // setIsLoading(false);
        })
        .catch(function (error) {
            // handle error
            // console.log(error);
            setData([]);
            // setIsLoading(false);
        })
        .finally(function () {
            // always executed
            // setIsLoading(false);
        });
    }

    useEffect(() => {
        // setIsLoading(true);
        blogs();
    }, [])

    return (
        <>
            <h3 className="pb-3">Blog</h3>
            <div className="g-4">

                {data && data.length ? (
                    <div class="row row-cols-1 row-cols-md-3 g-4">
                        {data.map((item, index) => {
                        
                            <div class="col">
                                <div class="card h-100">
                                    <img src="..." class="card-img-top" alt="..."/>
                                    <div class="card-body">
                                        <h5 class="card-title">Card title</h5>
                                        <p class="card-text">This is a longer card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                                    </div>
                                </div>
                            </div>
                        
                        })}
                    </div>
                ) : (<div>No data found</div>)}

                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Blog Name</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data && data.length ? (
                            data.map((item, index) => {
                                let blog = item.attributes;
                                // let imageURL = blog.image.data.attributes.formats.thumbnail.url;
                                // console.log(blog);
                                var imageURL = null;
                                if(blog.image.data && blog.image.data !== null){
                                    imageURL = blog.image.data.attributes.formats.thumbnail.url;
                                }
                                
                                return (
                                    <tr key={index}>
                                        <td>
                                            <div className="img-wrap">
                                                image
                                                </div>
                                        </td>
                                        <td>{blog.name}</td>

                                    </tr>
                                )
                            })
                        ) : (<tr><td colSpan={5}>No data found</td></tr>)}
                    </tbody>
                </Table>
            </div>
        </>
    )
}
