import config from './Config.json';
import axios from 'axios';

export default function UploadImage(token = null, image_data = null) {

    var blogImgData = null;

    if(image_data !== null && token !== null){

        try {
            let formData = new FormData();
            formData.append('files',image_data);

            let upload_response = axios({
                method: 'POST',
                url:`${config.baseURL}/api/upload`,
                headers: { 
                    'accept' : 'application/json',
                    'content-type': 'multipart/form-data',
                    "Authorization":`Bearer ${token}`
                },
                data: formData,
            });
            if(upload_response.status === 200){
                blogImgData = upload_response.data[0];
            }
            
        } catch (error) {
            blogImgData = null;
        }
    }

    return blogImgData;
}
