import React from 'react'
import $ from 'jquery';

function BtnLoader(event, status) {
    var defaultStatus = $(event.target).find('.btn-spin-status').html();

    if(status == 'show'){
        $(event.target).find('.btn-spin').parent().prop('disable', true);
        $(event.target).find('.btn-spin').addClass('show');
        $(event.target).find('.btn-spin-status').html("Loading...");
    }
    
    if(status == 'hide'){
        $(event.target).find('.btn-spin').parent().prop('disable', false);
        $(event.target).find('.btn-spin').removeClass('show');
        $(event.target).find('.btn-spin-status').html(defaultStatus);
    }
}

export {BtnLoader}
