import { useEffect } from 'react';
import config from './Config.json';
import axios from 'axios';

export default function UploadImage(token = null, image_data = null) {

    var uploadData = null;
    
    const handleUpload = async () => {
        if(image_data !== null && token !== null){

            try {
                let formData = new FormData();
                formData.append('files',image_data);
    
                let upload_response = await axios({
                    method: 'POST',
                    url:`${config.baseURL}/api/upload`,
                    headers: { 
                        'accept' : 'application/json',
                        'content-type': 'multipart/form-data',
                        "Authorization":`Bearer ${token}`
                    },
                    data: formData,
                });
                if(upload_response.status === 200){
                    uploadData = upload_response.data[0];
                }
                
            } catch (error) {
                uploadData = null;
            }
        }
    }

    useEffect(() => {
      handleUpload();
    }, [])
    
    return uploadData;
}

export function add(a, b) {
    return a + b;
}

export function upload(token = null, image_data = null) {
    return a + b;
}
