import axios from 'axios';
import React, { useState } from 'react'
import { Button, Col, Container, Form, Row } from 'react-bootstrap'
import { ToastContainer, toast } from 'react-toastify';
const config = require("../Config.json");

export default function Login() {

    const [identifier, setIdentifier] = useState('');
    const [password, setPassword] = useState('');

    var login = (e) => {
        e.preventDefault();
        console.log('login');
        console.log(identifier);
        console.log(password);

        if(identifier == ''){
            toast.error("Email is required");
            return false;
        }

        if(password == ''){
            toast.error("Password is required");
            return false;
        }

        axios.post(`${config.baseURL}/api/auth/local`, {
            identifier: identifier,
            password: password,
        })
        .then(response => {
            // Handle success.
            console.log('Well done!');
            console.log('User profile', response.data.user);
            console.log('User token', response.data.jwt);
        })
        .catch(error => {
            // Handle error.
            //console.log(error.response);
            const msg = error.response.data.error.message;
            toast.error(msg);
            return false;
        });
    }

    return (
        <Container className="mt-3">
            <Row>
                <Col xs={12} md={{ span: 6, offset: 3 }}>
                    <h3 className="text-center">Login</h3>
                    <Form>
                        <Form.Group className="mb-3" controlId="email">
                            <Form.Label>Email address</Form.Label>
                            <Form.Control type="text" placeholder="Enter email" name="email" value={identifier} onChange={(e)=>{setIdentifier(e.target.value)}} />
                        </Form.Group>

                        <Form.Group className="mb-3" controlId="password">
                            <Form.Label>Password</Form.Label>
                            <Form.Control type="password" placeholder="Password" name="password" value={password} onChange={(e)=>{setPassword(e.target.value)}} />
                        </Form.Group>
                    
                        <Button className="btn btn-info text-white" type="submit" onClick={(e)=>{login(e)}}>Login</Button>
                    </Form>
                </Col>
            </Row>
            <ToastContainer />
        </Container>
        
    )
}
