import { useEffect } from 'react';
import config from './Config.json';
import axios from 'axios';

export default function UploadImage(token = null, image_data = null) {

    if(image_data !== null && token !== null){
        async function postData(url = "", data) {
        
            await axios({
                method: 'POST',
                url:'http://localhost:1337/api/upload',
                headers: { 
                    'accept' : 'application/json',
                    'content-type': 'multipart/form-data',
                    "Authorization":`Bearer ${jwt.token}`
                },
                data: formData,
                onUploadProgress:(progress)=>{ 
                    console.log("FileUpload ", progress);
                }
            });
        }
        
        let formData = new FormData();
        formData.append('files',image_data);

        postData(`${config.baseURL}/api/upload`, formData).then((data) => {
            console.log(data);
        });

    }
}
