import React, { useEffect, useState } from 'react'
import Auth from './Auth'
import { Table } from 'react-bootstrap'
import axios from 'axios';
import Loader from '../Loader';
import config from './Config.json';

export default function BlogList() {

    const [data, setData] = useState([]);
    // const [currentDate, setCurrentDate] = useState(GetDate());
    const [isLoading, setIsLoading] = useState(false);

    let blogs = () => {
        axios.get('http://localhost:1337/api/blogs?populate=*')
        .then(function (response) {
            // handle success
            console.log(response.data);
            console.log(response.data.blogImage);
            setData(response.data.data);
            setIsLoading(false);
        })
        .catch(function (error) {
            // handle error
            // console.log(error);
            setData([]);
            setIsLoading(false);
        })
        .finally(function () {
            // always executed
            setIsLoading(false);
        });
    }

    useEffect(() => {
        setIsLoading(true);
        blogs();
    }, [])

    const deleteBlog = () => {

    }
    
    if(Auth()){
        return (
            <>
                {isLoading ? <Loader/> : null}
                <h4 className="pb-2">BlogList</h4>
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Blog Name</th>
                            <th>Published</th>
                            <th>Created On</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data && data.length ? (
                            data.map((item, index) => {
                                let blog = item.attributes;
                                // let imageURL = blog.image.data.attributes.formats.thumbnail.url;
                                // console.log(blog);
                                var imageURL = null;
                                if(blog.image.data && blog.image.data !== null){
                                    imageURL = blog.image.data.attributes.formats.thumbnail.url;
                                    console.log(imageURL);
                                }
                                
                                return (
                                    <tr key={index}>
                                        <td>
                                            {imageURL !== null ? <img className="img-thumb" src={config.baseURL+imageURL}/> : '-' }
                                        </td>
                                        <td>{blog.name}</td>
                                        <td>{blog.published}</td>
                                        <td>{blog.created_on}</td>
                                        <td>
                                            <a href={`/admin/edit-blog/${item.id}`} className="text-decoration-none text-success pr-5">Edit</a>
                                            <a className="text-decoration-none text-danger link-default" onClick={(e) => deleteBlog(e, blog.id)}>Delete</a>
                                        </td>
                                    </tr>
                                )
                            })
                        ) : (<tr><td colSpan={5}>No data found</td></tr>)}
                    </tbody>
                </Table>
            </>
        )
    }
}
