import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Card, Col, Row, Table } from 'react-bootstrap'

export default function Blog() {

    const [data, setData] = useState([]);

    let blogs = () => {
        axios.get('http://localhost:1337/api/blogs?populate=*')
        .then(function (response) {
            // handle success
            // console.log(response.data);
            // console.log(response.data.blogImage);
            setData(response.data.data);
            // setIsLoading(false);
        })
        .catch(function (error) {
            // handle error
            // console.log(error);
            setData([]);
            // setIsLoading(false);
        })
        .finally(function () {
            // always executed
            // setIsLoading(false);
        });
    }

    useEffect(() => {
        // setIsLoading(true);
        blogs();
    }, [])

    return (
        <>
            <h3 className="pb-3">Blog</h3>
            <Row xs={1} md={3} className="g-4">
                {data && data.length ? (
                        data.map((item, index) => {
                            let blog = item.attributes;
                            var imageURL = null;
                            if(blog.image.data && blog.image.data !== null){
                                imageURL = blog.image.data.attributes.formats.thumbnail.url;
                            }
                            <Col key={index}>
                                <Card>
                                    {imageURL !== null ? 
                                        <img className="img-thumb" src={config.baseURL+imageURL}/> 
                                        : <img className="img-thumb" src="/assets/img/no-image.jpg"/> 
                                    } 
                                    <Card.Img variant="top" src="holder.js/100px160" />
                                    <Card.Body>
                                        <Card.Title>Card title</Card.Title>
                                        <Card.Text>
                                            This is a longer card with supporting text below as a natural
                                            lead-in to additional content. This content is a little bit
                                            longer.
                                        </Card.Text>
                                    </Card.Body>
                                </Card>
                            </Col>
                        })
                ) : ('No blog available')}
            </Row>
        </>
    )
}
