import React, { useEffect, useState } from 'react'
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Button, Form } from 'react-bootstrap';
import GetDate from '../GetDate';
import { ToastContainer, toast } from 'react-toastify';
import Loader from '../Loader';

export default function EditBlog() {

    const { id } = useParams();
    // const [blog, setBlog] = useState([]);
    const [blogId, setBlogId] = useState("");
    const [blogName, setBlogName] = useState("");
    const [blogDescription, setBlogDescription] = useState("");
    const [blogPublished, setBlogPublished] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const [blogImage, setBlogImage] = useState(null);

    const handleBlogImageChange = (e) => {
        const file = e.target.files[0];
        console.log(file);
        setBlogImage(file);
    }

    const fetchBlog = () => {
        fetch('http://localhost:1337/api/blogs/'+id,{
            method : 'GET'
        })
        .then(response => response.json())
        .then((res) => {
            if(res.data){
                // console.log(res.data);
                setBlogId(res.data.id);
                let blogData = res.data.attributes;
                setBlogName(blogData.name);
                setBlogDescription(blogData.description);
                setBlogPublished(blogData.published);
                setIsLoading(false);
            }
        })
        .catch((error) => {
            console.log(error);
            setIsLoading(false);
        });
    }

    useEffect(() => {
        setIsLoading(true);
        fetchBlog();
    }, [])

    const uploadImageData = (id) => {
        alert('this block');

        
    }

    let handleSubmit = (e) => {
        e.preventDefault();
        let payload = {
            name : blogName,
            description : blogDescription,
            updated_on : GetDate(),
            published : blogPublished,
            // blogImage: blogImage
        }

        uploadImageData(id);
        return false;

        // Create a FormData object
        // const formData = new FormData();
        // formData.append("files", {"data":blogImage});
        // formData.append("name", blogName);
        // formData.append("description", blogDescription);
        // formData.append("published", blogPublished);
        // formData.append("updated_on", GetDate());

        axios({
            method: 'PUT',
            url: 'http://localhost:1337/api/blogs/'+id,
            data: {"data":payload},  
        })
        .then(function (response) {
            // handle success
            console.log(response.data);
            if(response.data){
                toast.success("Blog updated successfully");

                

                

                // window.location.href = "/admin/blog-list";
            }
            else{
                toast.error("Blog updation failed");
                return false;
            }
        })
        .catch(function (error) {
            // handle error
            console.log(error);
            toast.error(error.message);
            return false;
        });
    }

    const handlePublishChange = (e) => {
        setBlogPublished(e.target.value);
    }

    
    return (
        <>
            {isLoading ? <Loader/> : null}
            {blogId ? (
                <>
                    <Form onSubmit={(e) => handleSubmit(e)}>
                        <Form.Group className="mb-3" controlId="blogName">
                            <Form.Label>Name</Form.Label>
                            <Form.Control type="text" placeholder="Blog Name" value={blogName} onChange={(e)=>setBlogName(e.target.value)}/>
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="blogDescription">
                            <Form.Label>Description</Form.Label>
                            <Form.Control as="textarea" rows={5} placeholder="Blog Description" value={blogDescription} onChange={(e)=>setBlogDescription(e.target.value)}/>
                        </Form.Group>
                        <div key={`inline-radio`} className="mb-3">
                            <Form.Label className="label-margin">Published</Form.Label>
                            <Form.Check
                                inline
                                label="Yes"
                                name="published"
                                type="radio"
                                id={`inline-radio-1`}
                                value="yes"
                                checked={blogPublished === 'yes'}
                                onChange={(e) => handlePublishChange(e)}
                            />
                            <Form.Check
                                inline
                                label="No"
                                name="published"
                                type="radio"
                                id={`inline-radio-2`}
                                value="no"
                                checked={blogPublished === 'no'}
                                onChange={(e) => handlePublishChange(e)}
                            />
                        </div>
                        <Form.Group controlId="blogImage" className="mb-3">
                            <Form.Label>Blog Image</Form.Label>
                            <Form.Control type="file" name="file" accept="image/*" onChange={(e)=>handleBlogImageChange(e)}/>
                        </Form.Group>
                        <Button className="btn btn-success btn-margin" type="submit">Submit</Button>
                        <a type="button" href="/admin/blog-list" className="btn btn-secondary">Back</a>
                    </Form>
                </>
            ) : (<div>No data found</div>)}
            <ToastContainer/>
        </>
    )
}
