import { useEffect } from 'react';
import config from './Config.json';
import axios from 'axios';

export default async function UploadImage(token = null, image_data = null) {

    var uploadData = null;

    try {
        
        if(image_data !== null && token !== null){

            let formData = new FormData();
            formData.append('files',image_data);

            let upload_response = await axios({
                method: 'POST',
                url:'http://localhost:1337/api/upload',
                headers: { 
                    'accept' : 'application/json',
                    'content-type': 'multipart/form-data',
                    "Authorization":`Bearer ${token}`
                },
                data: formData,
                onUploadProgress:(progress)=>{ 
                    // console.log("FileUpload ", progress);
                }
            });
            if(upload_response.status === 200){
                // console.log('file upload response ',upload_response);
                console.log(upload_response.data[0]);
                uploadData = upload_response.data[0];
                return uploadData;
            }
        }
    } catch (error) {
        uploadData = null;
    }

    // console.log('uploadData: '+uploadData);

    return uploadData;
}
