import React, { useEffect, useState } from 'react'
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Button, Form } from 'react-bootstrap';
import GetDate from '../GetDate';
import { ToastContainer, toast } from 'react-toastify';
import Loader from '../Loader';

export default function EditBlog() {

    const { id } = useParams();
    // const [blog, setBlog] = useState([]);
    const [blogId, setBlogId] = useState("");
    const [blogName, setBlogName] = useState("");
    const [blogDescription, setBlogDescription] = useState("");
    const [blogPublished, setBlogPublished] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const [image, setImage] = useState(null);
    const [jwt, setJwt] = useState({
        token: ''
    });

    const handleImageChange = (event) => {
        const file = event.target.files[0];
        setImage(file);
    };

    const fetchBlog = () => {
        fetch('http://localhost:1337/api/blogs/'+id,{
            method : 'GET'
        })
        .then(response => response.json())
        .then((res) => {
            if(res.data){
                // console.log(res.data);
                setBlogId(res.data.id);
                let blogData = res.data.attributes;
                setBlogName(blogData.name);
                setBlogDescription(blogData.description);
                setBlogPublished(blogData.published);
                setIsLoading(false);
            }
        })
        .catch((error) => {
            console.log(error);
            setIsLoading(false);
        });
    }

    useEffect(() => {
        setIsLoading(true);
        fetchBlog();
    }, [])

    useEffect(()=>{
        try {
            let token = localStorage.getItem('token');
            if(token){
                //logged in
                setJwt({
                    ...jwt,
                    token:token
                })
            }else{
               
            }
            
        } catch (error) {
            
        }
       
    },[]);

    const handlePublishChange = (e) => {
        setBlogPublished(e.target.value);
    }
    const handleSubmit = async (event) => {
        event.preventDefault();
    
        try {
          // Upload the image to Strapi
          const formData = new FormData();
          formData.append('files', image);
    
          const uploadResponse = await fetch('http://localhost:1337/upload', {
            method: 'POST',
            body: formData,
          });
    
          const uploadData = await uploadResponse.json();
          const imageUrl = uploadData[0].url; // Assuming the URL of the uploaded image is available at index 0
    
          // Create the post
          const postData = {
            name : blogName,
            description : blogDescription,
            updated_on : GetDate(),
            published : blogPublished,
            featuredImage: imageUrl // Store the image URL as a string
          };
          
          console.log(postData);
          return false;

          const createResponse = await fetch('http://localhost:1337/api/blogs'+id, {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify(postData)
          });
    
          if (createResponse.ok) {
            console.log("Post created successfully");
            // Optionally, handle success response
          } else {
            console.error("Failed to create post");
            // Optionally, handle error response
          }
        } catch (error) {
          console.error("Error creating post:", error);
          // Handle network errors or other issues
        }
      };

    
    return (
        <>
            {isLoading ? <Loader/> : null}
            {blogId ? (
                <>
                    <Form onSubmit={(e) => handleSubmit(e)}>
                        <Form.Group className="mb-3" controlId="blogName">
                            <Form.Label>Name</Form.Label>
                            <Form.Control type="text" placeholder="Blog Name" value={blogName} onChange={(e)=>setBlogName(e.target.value)}/>
                        </Form.Group>
                        <Form.Group className="mb-3" controlId="blogDescription">
                            <Form.Label>Description</Form.Label>
                            <Form.Control as="textarea" rows={5} placeholder="Blog Description" value={blogDescription} onChange={(e)=>setBlogDescription(e.target.value)}/>
                        </Form.Group>
                        <div key={`inline-radio`} className="mb-3">
                            <Form.Label className="label-margin">Published</Form.Label>
                            <Form.Check
                                inline
                                label="Yes"
                                name="published"
                                type="radio"
                                id={`inline-radio-1`}
                                value="yes"
                                checked={blogPublished === 'yes'}
                                onChange={(e) => handlePublishChange(e)}
                            />
                            <Form.Check
                                inline
                                label="No"
                                name="published"
                                type="radio"
                                id={`inline-radio-2`}
                                value="no"
                                checked={blogPublished === 'no'}
                                onChange={(e) => handlePublishChange(e)}
                            />
                        </div>
                        <Form.Group controlId="blogImage" className="mb-3">
                            <Form.Label>Blog Image</Form.Label>
                            <Form.Control type="file" name="file" accept="image/*" onChange={handleImageChange}/>
                        </Form.Group>
                        <Button className="btn btn-success btn-margin" type="submit">Submit</Button>
                        <a type="button" href="/admin/blog-list" className="btn btn-secondary">Back</a>
                    </Form>
                </>
            ) : (<div>No data found</div>)}
            <ToastContainer/>
        </>
    )
}
