import logo from './logo.svg';
import { BrowserRouter as Router, Routes, Route, Navigate} from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import { useEffect, useState } from 'react';
import Dashboard from './components/Dashboard';


console.log(localStorage.getItem('isLoggedIn'));

const isAuthenticated = () => {
  // if(localStorage.getItem('isLoggedIn') == true){
  //   return true;
  // }
  // return false;
  return localStorage.getItem('isLoggedIn');
};

var isLoggedIn = isAuthenticated();
console.log(isLoggedIn);
if(isLoggedIn === true){
  console.log('1');
}
else{
  console.log('2');
}

function App() {  
  return (
    <Router>
      <Routes>
        <Route path="/" element={isAuthenticated() === true ? <Navigate replace to="/dashboard" /> : <Login/>}></Route>
        <Route path="/login" element={isAuthenticated() === true ? <Navigate replace to="/dashboard"/> : <Login/>}></Route>
        <Route path="/register" element={<Register/>}></Route>
        <Route path="/dashboard" element={isAuthenticated() === true ? <Dashboard/>  : <Navigate replace to="/"/> } />
      </Routes>
    </Router>
  );
}

export default App;
