import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Card, Col, Row, Table } from 'react-bootstrap'

export default function Blog() {

    const [data, setData] = useState([]);

    let blogs = () => {
        axios.get('http://localhost:1337/api/blogs?populate=*')
        .then(function (response) {
            // handle success
            // console.log(response.data);
            // console.log(response.data.blogImage);
            setData(response.data.data);
            // setIsLoading(false);
        })
        .catch(function (error) {
            // handle error
            // console.log(error);
            setData([]);
            // setIsLoading(false);
        })
        .finally(function () {
            // always executed
            // setIsLoading(false);
        });
    }

    useEffect(() => {
        // setIsLoading(true);
        blogs();
    }, [])

    return (
        <>
            <h3 className="pb-3">Blog</h3>
            <Row xs={1} md={3} className="g-4">
                {data && data.length ? (
                    data.map((item, index) => {
                        let blog = item.attributes;
                        console.log(blog);
                        console.log(blog.name);
                        var imageURL = null;
                        if(blog.image.data && blog.image.data !== null){
                            imageURL = blog.image.data.attributes.formats.thumbnail.url;
                        }
                        console.log(imageURL);
                        <Col key={index}>
                            <Card>
                                <Card.Img variant="top" src="holder.js/100px160" />
                                <Card.Body>
                                    <Card.Title>{blog.name}</Card.Title>
                                    <Card.Text>
                                        {blog.description}
                                    </Card.Text>
                                </Card.Body>
                            </Card>
                        </Col>
                    })
                ) : ('No blog available')}
            </Row>
        </>
    )
}
