import React, { useEffect, useState } from 'react'

export default function Auth() {

    const [loggedIn, setLoggedIn] = useState(false);

    useEffect(() => {
        if(localStorage.getItem('isLoggedIn') && localStorage.getItem('isLoggedIn') == 'true'){
          setLoggedIn(true);
        }
        else{
          setLoggedIn(false);
        }
    }, [])

    return loggedIn;
}
